(210, 210, 'Gina''s Desk Tasks Lvl2', '<?xml version="1.0" encoding="utf-8" ?>\r\n\r\n<npc xmlns="http://tempuri.org/NpcTemplate.xsd">\r\n  \r\n  <objectInfo>\r\n    <id>210</id>\r\n    <name>Gina''s Tasks</name>\r\n  </objectInfo>\r\n  \r\n  <graphics>\r\n	  <sprite>\r\n		  <image id="0" name="note.png"/>\r\n		  <tileSize height="50" width="50"/>\r\n	  </sprite>\r\n	  \r\n	  <smallTalks>\r\n		  <talk>*you see nothing of interest*</talk>\r\n	  </smallTalks>\r\n	  \r\n	  <animation>\r\n		  <anim id="0" desc="stand">\r\n			  <script nr="0" src="0"/>\r\n		  </anim> \r\n	  </animation>\r\n	  \r\n	  <collisionArea>\r\n		  <circle x="0" y="-10" radius="10"/>\r\n	  </collisionArea>\r\n	  \r\n	  <touchArea>\r\n		  <circle x="0" y="-10" radius="30"/>\r\n	  </touchArea>\r\n	  \r\n  </graphics>\r\n  \r\n  <statemachine>    \r\n\r\n  <state id="0" name="invisible">\r\n      <animId>0</animId>\r\n      <movement>stand</movement>\r\n      <text></text>\r\n      <question></question>      \r\n      <visible>false</visible>\r\n      <walkable>true</walkable>\r\n      <walkDest>0</walkDest>\r\n      <events>\r\n	      <triggerEvent nextStateId="1"></triggerEvent>\r\n	      <!-- wait for trigger from Gina to become visible. It''s the second assignment the player gets from Gina -->\r\n      </events>\r\n      <autoTalk>false</autoTalk>\r\n  </state>\r\n  \r\n  <state id="1" name="visible 1">\r\n	<shadowVisible>false</shadowVisible>\r\n      <animId>0</animId>      \r\n      <text>\r\n        <p>Hello,</p>\r\n        <p>The first task for you is to search for a special word in a document. Unfortunatly I forgot the name of the file that contains the word.</p>\r\n        <p>Also we have to document our company network so we need to know the Linux version of our webserver.</p>        \r\n      </text>\r\n      <question></question>\r\n      <answers></answers>\r\n      <walkable>true</walkable>\r\n      <visible>true</visible>\r\n      <walkDest>0</walkDest>\r\n      <movement>stand</movement>\r\n      <events>	\r\n        <talk nextStateId="2" />\r\n      </events>\r\n      <autoTalk>false</autoTalk>\r\n    </state> \r\n    \r\n    <state id="2" name="visible 2">\r\n	   <shadowVisible>false</shadowVisible>\r\n      <animId>0</animId>      \r\n      <text>\r\n        <p>The exact description of these tasks can be found in your scenario section on your PDA. Thank you very much for your hard work. You are really helping me out a lot over here. If you want to have a coffee you can call me anytime</p>\r\n        <p>Love Gina...</p>\r\n      </text>\r\n      <question></question>\r\n      <answers></answers>\r\n      <walkable>true</walkable>\r\n      <visible>true</visible>\r\n      <walkDest>0</walkDest>\r\n      <movement>stand</movement>\r\n      <events>	\r\n        <talk nextStateId="3">\r\n          <triggers>\r\n            <trigger eventType="triggerEvent" machineId="206" />\r\n						<!-- activate phil -->\r\n            <skillTrigger skillId="10" />\r\n            <skillTrigger skillId="11" />\r\n          </triggers>\r\n        </talk>\r\n      </events>\r\n      <autoTalk>true</autoTalk>\r\n    </state>\r\n    \r\n    <state id="3" name="visible, waiting for scenario complete">\r\n	    <shadowVisible>false</shadowVisible>\r\n	    <animId>0</animId>      \r\n	    <text>\r\n        <p>(You are already on these tasks)</p>        \r\n        <p>The first task for you is to search for a special word in a document. Unfortunatly I forgot the name of the file that contains the word.</p>\r\n        <p>Also we have to document our company network so we need to know the Linux version of our webserver.</p> \r\n      </text>\r\n	    <question></question>\r\n	    <answers></answers>\r\n	    <walkable>true</walkable>\r\n	    <visible>true</visible>\r\n	    <walkDest>0</walkDest>\r\n	    <movement>stand</movement>\r\n	    <events>\r\n        <scenarioCompletedEvent scenarioId="142" nextStateId="4" />\r\n        <scenarioCompletedEvent scenarioId="136" nextStateId="5" />\r\n	    </events>\r\n	    <autoTalk>false</autoTalk>\r\n    </state>\r\n    \r\n    <state id="4" name="visible, waiting for scenario complete, 8 done">\r\n	    <shadowVisible>false</shadowVisible>\r\n	    <animId>0</animId>      \r\n	    <text>\r\n        <p>(You have one task left)</p>\r\n        <p>We have to document our company network so we need to know the Linux version of our webserver.</p>\r\n      </text>\r\n	    <question></question>\r\n	    <answers></answers>\r\n	    <walkable>true</walkable>\r\n	    <visible>true</visible>\r\n	    <walkDest>0</walkDest>\r\n	    <movement>stand</movement>\r\n	    <events>\r\n        <scenarioCompletedEvent scenarioId="136" nextStateId="6">\r\n          <triggers>\r\n            <trigger eventType="triggerEvent" machineId="208"></trigger>\r\n						<!-- activate gina -->\r\n					</triggers>\r\n				</scenarioCompletedEvent>\r\n	    </events>\r\n	    <autoTalk>false</autoTalk>\r\n    </state>\r\n    \r\n    <state id="5" name="visible, waiting for scenario complete, 9 done">\r\n	    <shadowVisible>false</shadowVisible>\r\n	    <animId>0</animId>      \r\n	    <text>\r\n        <p>(You have one task left)</p>\r\n        <p>You have to search for a special word in a document. Unfortunatly I forgot the name of the file that contains the word.</p>\r\n      </text>\r\n	    <question></question>\r\n	    <answers></answers>\r\n	    <walkable>true</walkable>\r\n	    <visible>true</visible>\r\n	    <walkDest>0</walkDest>\r\n	    <movement>stand</movement>\r\n	    <events>\r\n        <scenarioCompletedEvent scenarioId="142" nextStateId="6">\r\n          <triggers>\r\n            <trigger eventType="triggerEvent" machineId="208"></trigger>\r\n						<!-- activate gina -->\r\n					</triggers>\r\n				</scenarioCompletedEvent>\r\n	    </events>\r\n	    <autoTalk>false</autoTalk>\r\n    </state>\r\n    \r\n    <state id="6" name="invisible, final">\r\n	    <animId>0</animId>\r\n	    <movement>stand</movement>\r\n	    <text></text>\r\n	    <question></question>      \r\n	    <visible>false</visible>\r\n	    <walkable>true</walkable>\r\n	    <walkDest>0</walkDest>\r\n	    <events></events>\r\n	    <autoTalk>false</autoTalk>\r\n  </state> \r\n    \r\n  </statemachine>\r\n  \r\n</npc>', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
