(211, 211, 'Phil''s Desk Task Lvl2', '<?xml version="1.0" encoding="utf-8" ?>\r\n\r\n<npc xmlns="http://tempuri.org/NpcTemplate.xsd">\r\n  \r\n  <objectInfo>\r\n    <id>211</id>\r\n    <name>Phil''s Task</name>\r\n  </objectInfo>\r\n  \r\n  <graphics>\r\n	  <sprite>\r\n		  <image id="0" name="note.png"/>\r\n		  <tileSize height="50" width="50"/>\r\n	  </sprite>\r\n	  \r\n	  <smallTalks>\r\n		  \r\n	  </smallTalks>\r\n	  \r\n	  <animation>\r\n		  <anim id="0" desc="stand">\r\n			  <script nr="0" src="0"/>\r\n		  </anim> \r\n	  </animation>\r\n	  \r\n	  <collisionArea>\r\n		  <circle x="0" y="-10" radius="10"/>\r\n	  </collisionArea>\r\n	  \r\n	  <touchArea>\r\n		  <circle x="0" y="-10" radius="30"/>\r\n	  </touchArea>\r\n	  \r\n  </graphics>\r\n  \r\n  <statemachine>    \r\n\r\n  <state id="0" name="invisible">\r\n      <animId>0</animId>\r\n      <movement>stand</movement>\r\n      <text></text>\r\n      <question></question>      \r\n      <visible>false</visible>\r\n      <walkable>true</walkable>\r\n      <walkDest>0</walkDest>\r\n      <events>\r\n	      <triggerEvent nextStateId="1"></triggerEvent>\r\n	      <!-- wait for trigger from phil to become visible. It''s the first assignment the player gets of Phil -->\r\n      </events>\r\n      <autoTalk>false</autoTalk>\r\n  </state>   \r\n    \r\n    <state id="1" name="visible">\r\n	<shadowVisible>false</shadowVisible>\r\n      <animId>0</animId>      \r\n      <text>\r\n        <p>Hello,</p>\r\n        <p>shortly we decided that we want to set up our own new mailserver to get rid of all the spam mails we get at the moment. Therefore we need to setup new DNS Server Entries so that our mailserver can be used from the outside.</p>\r\n        <p>The exact description of these tasks can be found in your scenario section on your PDA. Thank you very much.</p>\r\n        <p>Cheers, Phil</p>\r\n      </text>\r\n      <question></question>\r\n      <answers></answers>\r\n      <walkable>true</walkable>\r\n      <visible>true</visible>\r\n      <walkDest>0</walkDest>\r\n      <movement>stand</movement>\r\n      <events>	\r\n        <talk nextStateId="2">\r\n          <triggers>\r\n            <skillTrigger skillId="12" />\r\n            <!-- Skill für Scenario 128 -->\r\n            <trigger eventType="triggerEvent" machineId="206"></trigger>\r\n						<!-- activate phil -->\r\n          </triggers>\r\n        </talk>\r\n      </events>\r\n      <autoTalk>false</autoTalk>\r\n    </state>\r\n    \r\n    <state id="2" name="visible, waiting for scenario complete">\r\n	    <shadowVisible>false</shadowVisible>\r\n	    <animId>0</animId>      \r\n	    <text>\r\n        <p>(You are already on this task)</p>\r\n        <p>Shortly we decided that we want to set up our own new mailserver to get rid of all the spam mails we get at the moment. Therefore we need to setup new DNS Server Entries so that our mailserver can be used from the outside.</p>\r\n      </text>\r\n	    <question></question>\r\n	    <answers></answers>\r\n	    <walkable>true</walkable>\r\n	    <visible>true</visible>\r\n	    <walkDest>0</walkDest>\r\n	    <movement>stand</movement>\r\n	    <events>\r\n        <scenarioCompletedEvent scenarioId="128" nextStateId="3" />\r\n	    </events>\r\n	    <autoTalk>false</autoTalk>\r\n    </state>\r\n    \r\n    <state id="3" name="invisible, final">\r\n	    <animId>0</animId>\r\n	    <movement>stand</movement>\r\n	    <text></text>\r\n	    <question></question>      \r\n	    <visible>false</visible>\r\n	    <walkable>true</walkable>\r\n	    <walkDest>0</walkDest>\r\n	    <events></events>\r\n	    <autoTalk>false</autoTalk>\r\n  </state> \r\n    \r\n  </statemachine>\r\n  \r\n</npc>', '', '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(208, 208, 'Gina Lvl2', '<?xml version="1.0" encoding="utf-8" ?>\r\n\r\n<npc xmlns="http://tempuri.org/NpcTemplate.xsd">\r\n	<objectInfo>\r\n		<id>208</id>\r\n		<name>Gina</name>\r\n	</objectInfo>\r\n	\r\n	<graphics>\r\n		\r\n		<sprite>\r\n			<image id="0" name="gina.png"/>\r\n			<tileSize height="64" width="32"/>\r\n		</sprite>\r\n		\r\n		<collisionArea>\r\n			<circle x="0" y="-10" radius="10"/>\r\n		</collisionArea>\r\n		<touchArea>\r\n			<circle x="0" y="-20" radius="30"/>\r\n		</touchArea>\r\n		\r\n		<smallTalks></smallTalks>\r\n		\r\n		<animation>\r\n			<anim id="0" desc="stand">\r\n				<script nr="0" src="7"/>\r\n				<script nr="1" src="4"/>\r\n				<script nr="2" src="10"/>\r\n				<script nr="3" src="1"/>\r\n			</anim>\r\n			<anim id="1" desc="walk">\r\n				<script nr="0" src=":6-8,100"/>\r\n				<script nr="1" src=":3-5,100"/>\r\n				<script nr="2" src=":9-11,100"/>\r\n				<script nr="3" src=":0-2,100"/>\r\n			</anim>\r\n		</animation>\r\n	</graphics>\r\n	\r\n	<statemachine>\r\n		\r\n		<state id="0" name="first encounter in level 2, 1">\r\n			<animId>0</animId>\r\n			<movement>stand</movement>\r\n			<text>\r\n				<p>Hey, finally you are with us. We are really glad to have you here. I think you''ll be a fine addition to our team.</p>\r\n			</text>            \r\n			<question>Do you need a tour through the department?</question>\r\n			<answers>\r\n				<answer id="0" text="Yes, a tour would be nice."/>\r\n				<answer id="1" text="No, I think I don''t need a tour."/>\r\n			</answers>\r\n			<events>\r\n				<answerEvent answerId="0" nextStateId="1"></answerEvent>\r\n				<answerEvent answerId="1" nextStateId="100"></answerEvent>\r\n			</events>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>0</walkDest>      \r\n			<autoTalk>true</autoTalk>\r\n		</state>  \r\n		\r\n		<state id="1" name="first encounter in level 2, 2a">\r\n			<animId>0</animId>\r\n			<movement>stand</movement>\r\n			<text>\r\n				<p>Okay, perfect! Let''s get started then. I will show you around in this department and afterwards you''ll get your first tasks from me. Just follow me.</p>\r\n			</text>            \r\n			<question></question>\r\n			<answers></answers>\r\n			<events>\r\n				<talk nextStateId="2">         \r\n				</talk>\r\n			</events>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>0</walkDest>      \r\n			<autoTalk>true</autoTalk>\r\n		</state>   \r\n		\r\n		<state id="2" name="walking to break room">\r\n			<animId>1</animId>\r\n			<answers></answers>\r\n			<movement>destination</movement>\r\n			<question></question>\r\n			<text>Follow me, newbie!</text>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>3</walkDest>\r\n			<events>\r\n				<reachDest nextStateId="3"/>              \r\n			</events>\r\n			<autoTalk>false</autoTalk>\r\n		</state>\r\n		\r\n		<state id="3" name="showing the break room">\r\n			<animId>0</animId>\r\n			<question></question>\r\n			<movement>stand</movement>\r\n			<text>\r\n				<p>This is our break room. Here you can get yourself some coffee or some food from the fridge but please clean up afterwards. If you are looking for some extra work you should have a look at the black board. Okay, I will show you our conference room now. Follow me.</p>\r\n			</text>\r\n			<visible>true</visible>\r\n			<walkable>false</walkable>\r\n			<walkDest>0</walkDest>\r\n			<events>\r\n				<talk nextStateId="130"></talk>\r\n			</events>\r\n			<autoTalk>false</autoTalk>\r\n		</state>\r\n		\r\n		<state id="130" name="walking to conference room">\r\n			<animId>1</animId>\r\n			<answers></answers>\r\n			<movement>destination</movement>\r\n			<question></question>\r\n			<text>Next stop: conference room!</text>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>13</walkDest>\r\n			<events>\r\n				<reachDest nextStateId="4"/>              \r\n			</events>\r\n			<autoTalk>false</autoTalk>\r\n		</state>\r\n		\r\n		<state id="4" name="showing the conference room, 1">\r\n			<animId>0</animId>\r\n			<movement>stand</movement>\r\n			<text>\r\n				<p>Okay, this is our conference room. Here we have our daily meetings where everybody has to be present to talk about his current projects.</p>\r\n			</text>\r\n			<question>Any questions?</question>\r\n			<answers>\r\n				<answer id="0" text="Um...yeah... at what time are these meetings?"/>\r\n			</answers>\r\n			<events>\r\n				<answerEvent answerId="0" nextStateId="5"></answerEvent>\r\n			</events>\r\n			<visible>true</visible>\r\n			<walkable>false</walkable>\r\n			<walkDest>0</walkDest>\r\n			<autoTalk>false</autoTalk>\r\n		</state>\r\n		\r\n		<state id="5" name="showing the conference room, 2">\r\n			<animId>0</animId>\r\n			<movement>stand</movement>\r\n			<text><p>We usually start around 8am.</p></text>\r\n			<question>Something wrong with that?</question>\r\n			<answers>\r\n				<answer id="0" text="Wow, 8am? That''s early..."/>\r\n			</answers>\r\n			<events>\r\n				<answerEvent answerId="0" nextStateId="6"></answerEvent>\r\n			</events>\r\n			<visible>true</visible>\r\n			<walkable>false</walkable>\r\n			<walkDest>0</walkDest>\r\n			<autoTalk>true</autoTalk>\r\n		</state>\r\n		\r\n		<state id="6" name="walking to server room">\r\n			<animId>0</animId>\r\n			<answers></answers>\r\n			<movement>stand</movement>\r\n			<question></question>\r\n			<text>Really?! I start working at 6am so it''s late for me. Come on I show you the server room.</text>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest></walkDest>\r\n			<events>\r\n				<talk nextStateId="7"/>              \r\n			</events>\r\n			<autoTalk>true</autoTalk>\r\n		</state>\r\n		\r\n		<state id="7" name="walking to server room">\r\n			<animId>1</animId>\r\n			<answers></answers>\r\n			<movement>destination</movement>\r\n			<question></question>\r\n			<text>Next stop: server room!</text>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>12</walkDest>\r\n			<events>\r\n				<reachDest nextStateId="8"/>              \r\n			</events>\r\n			<autoTalk>false</autoTalk>\r\n		</state>\r\n		\r\n		<state id="8" name="showing the server room, 1">\r\n			<animId>0</animId>\r\n			<question></question>\r\n			<movement>stand</movement>\r\n			<text>\r\n				<p>Pretty hot in here, eh? We have a lot of servers that are running 24/7. If you have any problems with our servers or you need to work directly on a console you should talk to Phil. He is our server admin and can help you with your issues.</p>\r\n				<p>Next stop is your desk!</p>\r\n			</text>\r\n			<visible>true</visible>\r\n			<walkable>false</walkable>\r\n			<walkDest>0</walkDest>\r\n			<events>\r\n				<talk nextStateId="9"></talk>\r\n			</events>\r\n			<autoTalk>false</autoTalk>\r\n		</state>\r\n		\r\n		<state id="9" name="walking to players desk">\r\n			<animId>1</animId>\r\n			<answers></answers>\r\n			<movement>destination</movement>\r\n			<question></question>\r\n			<text>Next stop is your desk!</text>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>11</walkDest>\r\n			<events>\r\n				<reachDest nextStateId="10"/>              \r\n			</events>\r\n			<autoTalk>false</autoTalk>\r\n		</state> \r\n		\r\n		<state id="10" name="at players desk, 1">\r\n			<animId>0</animId>\r\n			<movement>stand</movement>\r\n			<text>\r\n				<p>Okay, this is your desk. There is your computer and all the stuff you need. Your colleagues will place some tasks here for you later.</p>\r\n			</text>            \r\n			<question>Do you like it?</question>\r\n			<answers>\r\n				<answer id="0" text="It''s okay...*cough*"/>\r\n				<answer id="1" text="This is a desk?!"/>\r\n			</answers>\r\n			<events>\r\n				<answerEvent answerId="0" nextStateId="11"></answerEvent>\r\n				<answerEvent answerId="1" nextStateId="12"></answerEvent>\r\n			</events>\r\n			<visible>true</visible>\r\n			<walkable>true</walkable>\r\n			<walkDest>0</walkDest>      \r\n			<autoTalk>false</autoTal